mapper.update:
update employees
set last_name = #{last_name}, first_name = #{first_name}
	, email = #{email}, phone_number = #{phone_number}
	, salary = #{salary}, job_id = #{job_id}, hire_date = #{hire_date}
	, department_id = #{department_id}
	, manager_id = #{manager_id}
where employee_id = #{employee_id}

mapper.select:
select last_name||' '|| first_name name, department_name , job_title , e.* from employees e left outer join departments d on e.department_id=d.department_id inner join jobs j on e.job_id=j.job_id
where e.department_id is null where e.department_id = #{department_id}
order by employee_id
